}));

// Example 2
const variantIcon = {
  success: CheckCircleIcon,
  warning: WarningIcon,
  error: ErrorIcon,
  info: InfoIcon
};

const useStyles21 = makeStyles(theme => ({
  success: {
    backgroundColor: green[600]
  },
