                open={state7.open7}
                onClose={handleClose7}
                TransitionComponent={state7.Transition7}
                ContentProps={{
                  "aria-describedby": "message-id"
                }}
                message={<span id="message-id">I love snacks</span>}
              />
            </div>
          </KTCodeExample>
