                                                                  <MenuItem
                                                                    key={
                                                                      option.value
                                                                    }
                                                                    value={
                                                                      option.value
                                                                    }
                                                                  >
                                                                    {
                                                                      option.label
                                                                    }
                                                                  </MenuItem>
                                                                )
                                                              )}
                                                            </TextField>

                                                            <TextField
                                                              error
                                                              id="outlined-error"
                                                              label="Equipment"
                                                              className={
                                                                classes2.textField
                                                              }
                                                              margin="normal"
                                                              variant="outlined"
                                                            />
                                                            <TextField
                                                              error
                                                              id="outlined-error"
                                                              label="Quantity"
                                                              className={
                                                                classes2.textField
                                                              }
                                                              margin="normal"
                                                              variant="outlined"
                                                            />

