import axios from "axios";

export const LOGIN_URL = 'http://13.232.102.139:9000/user/login';
export const REGISTER_URL = "api/auth/register";
export const REQUEST_PASSWORD_URL = "api/auth/forgot-password";
export const ME_URL = `${process.env.REACT_APP_API_URL}/auth/me`;

export function login(email, password) {
  const requestOptions = {
    method: 'POST',
    headers: { 'Content-Type': 'application/json','Authorization': 'Bearer nFMjOROg5xdWVt1gG0wAcWdEd' },
    body: JSON.stringify({"email":email, "password":password})
};
  return fetch(LOGIN_URL, requestOptions)
  .then(response => response.json())
  .then(data => ({email,password}));;
}

export function register(email, fullname, username, password) {
  return axios.post(REGISTER_URL, { email, fullname, username, password });
}

export function requestPassword(email) {
  return axios.post(REQUEST_PASSWORD_URL, { email });
}

export function getUserByToken() {
  // Authorization head should be fulfilled in interceptor.
  return axios.get(ME_URL);
}
