/* eslint-disable no-restricted-imports */
import React from "react";
import PropTypes from "prop-types";
import Draggable from "react-draggable";
import {makeStyles, withStyles, useTheme} from "@material-ui/core/styles";
import {
  Dialog,
  DialogTitle,
  List,
  ListItem,
  ListItemAvatar,
  Avatar,
  ListItemText,
  Typography,
  Button,
  DialogActions,
  DialogContent,
  DialogContentText,
  Slide,
  TextField,
  IconButton,
  AppBar,
  Toolbar,
  Divider,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  FormControlLabel,
  Switch,
  useMediaQuery,
  RadioGroup,
  Radio,
  Paper
} from "@material-ui/core";
import { blue } from "@material-ui/core/colors";
import PersonIcon from "@material-ui/icons/Person";
import AddIcon from "@material-ui/icons/Add";
import CloseIcon from "@material-ui/icons/Close";
import {Notice, KTCodeExample} from "../../../../_metronic/_partials/controls";

// Example 1
const emails = ["username@gmail.com", "user02@gmail.com"];
const useStyles = makeStyles({
  avatar: {
    backgroundColor: blue[100],
    color: blue[600]
  }
});

function SimpleDialog(props) {
  const classes = useStyles();
  const { onClose, selectedValue, ...other } = props;

  function handleClose() {
    onClose(selectedValue);
  }

  function handleListItemClick(value) {
    onClose(value);
  }

  return (
    <Dialog
      onClose={handleClose}
      aria-labelledby="simple-dialog-title"
      {...other}
    >
      <DialogTitle id="simple-dialog-title">Set backup account</DialogTitle>
      <List>
        {emails.map(email => (
          <ListItem
            button
            onClick={() => handleListItemClick(email)}
            key={email}
          >
            <ListItemAvatar>
              <Avatar className={classes.avatar}>
                <PersonIcon />
              </Avatar>
            </ListItemAvatar>
            <ListItemText primary={email} />
          </ListItem>
        ))}

        <ListItem button onClick={() => handleListItemClick("addAccount")}>
          <ListItemAvatar>
            <Avatar>
              <AddIcon />
            </Avatar>
          </ListItemAvatar>
          <ListItemText primary="add account" />
        </ListItem>
      </List>
    </Dialog>
  );
}

SimpleDialog.propTypes = {
  onClose: PropTypes.func,
  open: PropTypes.bool,
  selectedValue: PropTypes.string
};

// Example 3
const Transition3 = React.forwardRef(function Transition5(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

// Example 5
const styles5 = theme => ({
  root: {
    margin: 0,
    padding: theme.spacing(2)
  },
  closeButton: {
    position: "absolute",
    right: theme.spacing(1),
    top: theme.spacing(1),
    color: theme.palette.grey[500]
  }
});

const DialogTitle5 = withStyles(styles5)(props => {
  const { children, classes, onClose } = props;
  return (
    <DialogTitle disableTypography className={classes.root}>
      <Typography variant="h6">{children}</Typography>
      {onClose ? (
        <IconButton
          aria-label="Close"
          className={classes.closeButton}
          onClick={onClose}
        >
          <CloseIcon />
        </IconButton>
      ) : null}
    </DialogTitle>
  );
});

const DialogContent5 = withStyles(theme => ({
  root: {
    padding: theme.spacing(2)
  }
}))(DialogContent);

const DialogActions5 = withStyles(theme => ({
  root: {
    margin: 0,
    padding: theme.spacing(1)
  }
}))(DialogActions);

// Example 6
const useStyles6 = makeStyles(theme => ({
  appBar: {
    position: "relative"
  },
  title: {
    marginLeft: theme.spacing(2),
    flex: 1
  }
}));

const Transition6 = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

// Example 7
const useStyles7 = makeStyles(theme => ({
  form: {
    display: "flex",
    flexDirection: "column",
    margin: "auto",
    width: "fit-content"
  },
  formControl: {
    marginTop: theme.spacing(2),
    minWidth: 120
  },
  formControlLabel: {
    marginTop: theme.spacing(1)
  }
}));

// Example 9
const options9 = [
  "None",
  "Atria",
  "Callisto",
  "Dione",
  "Ganymede",
  "Hangouts Call",
  "Luna",
  "Oberon",
  "Phobos",
  "Pyxis",
  "Sedna",
  "Titania",
  "Triton",
  "Umbriel"
];

function ConfirmationDialogRaw9(props) {
  const { onClose, value: valueProp, open, ...other } = props;
  const [value, setValue] = React.useState(valueProp);
  const radioGroupRef = React.useRef(null);

  React.useEffect(() => {
    if (!open) {
      setValue(valueProp);
    }
  }, [valueProp, open]);

  function handleEntering() {
    if (radioGroupRef.current != null) {
      radioGroupRef.current.focus();
    }
  }

  function handleCancel() {
    onClose();
  }

  function handleOk() {
    onClose(value);
  }

  function handleChange(event, newValue) {
    setValue(newValue);
  }

  return (
    <Dialog
      disableBackdropClick
      disableEscapeKeyDown
      maxWidth="xs"
      onEntering={handleEntering}
      aria-labelledby="confirmation-dialog-title"
      open={open}
      {...other}
    >
      <DialogTitle id="confirmation-dialog-title">Phone Ringtone</DialogTitle>
      <DialogContent dividers>
        <RadioGroup
          ref={radioGroupRef}
          aria-label="Ringtone"
          name="ringtone"
          value={value}
          onChange={handleChange}
        >
          {options9.map(option => (
            <FormControlLabel
              value={option}
              key={option}
              control={<Radio />}
              label={option}
            />
          ))}
        </RadioGroup>
      </DialogContent>
      <DialogActions>
        <Button onClick={handleCancel} color="primary">
          Cancel
        </Button>
        <Button onClick={handleOk} color="primary">
          Ok
        </Button>
      </DialogActions>
    </Dialog>
  );
}

ConfirmationDialogRaw9.propTypes = {
  onClose: PropTypes.func.isRequired,
  open: PropTypes.bool.isRequired,
  value: PropTypes.string.isRequired
};

const useStyles9 = makeStyles(theme => ({
  root: {
    width: "100%",
    maxWidth: 360,
    backgroundColor: theme.palette.background.paper
  },
  paper: {
    width: "80%",
    maxHeight: 435
  }
}));

// Example 11
function PaperComponent11(props) {
  return (
    <Draggable cancel={'[class*="MuiDialogContent-root"]'}>
      <Paper {...props} />
    </Draggable>
  );
}

export default function DialogsExamplesPage() {
  // Example 1
  const [open, setOpen] = React.useState(false);
  const [selectedValue, setSelectedValue] = React.useState(emails[1]);

  function handleClickOpen() {
    setOpen(true);
  }

  const handleClose = value => {
    setOpen(false);
    setSelectedValue(value);
  };

  // Example 2
  const [open2, setOpen2] = React.useState(false);

  function handleClickOpen2() {
    setOpen2(true);
  }

  function handleClose2() {
    setOpen2(false);
  }

  // Example 3
  const [open3, setOpen3] = React.useState(false);

  function handleClickOpen3() {
    setOpen3(true);
  }

  function handleClose3() {
    setOpen3(false);
  }

  // Example 4
  const [open4, setOpen4] = React.useState(false);

  function handleClickOpen4() {
    setOpen4(true);
  }

  function handleClose4() {
    setOpen4(false);
  }

  // Example 5
  const [open5, setOpen5] = React.useState(false);

  function handleClickOpen5() {
    setOpen5(true);
  }

  function handleClose5() {
    setOpen5(false);
  }

  // Example 6
  const classes6 = useStyles6();
  const [open6, setOpen6] = React.useState(false);

  function handleClickOpen6() {
    setOpen6(true);
  }

  function handleClose6() {
    setOpen6(false);
  }

  // Example 7
  const classes7 = useStyles7();
  const [open7, setOpen7] = React.useState(false);
  const [fullWidth7, setFullWidth7] = React.useState(true);
  const [maxWidth7, setMaxWidth7] = React.useState("sm");

  function handleClickOpen7() {
    setOpen7(true);
  }

  function handleClose7() {
    setOpen7(false);
  }

  function handleMaxWidthChange7(event) {
    setMaxWidth7(event.target.value);
  }

  function handleFullWidthChange7(event) {
    setFullWidth7(event.target.checked);
  }

  // Example 8
  const [open8, setOpen8] = React.useState(false);
  const theme8 = useTheme();
  const fullScreen8 = useMediaQuery(theme8.breakpoints.down("sm"));

  function handleClickOpen8() {
    setOpen8(true);
  }

  function handleClose8() {
    setOpen8(false);
  }

  // Example 9
  const classes9 = useStyles9();
  const [open9, setOpen9] = React.useState(false);
  const [value9, setValue9] = React.useState("Dione");

  function handleClickListItem9() {
    setOpen9(true);
  }

  function handleClose9(newValue) {
    setOpen9(false);

    if (newValue) {
      setValue9(newValue);
    }
  }

  // Example 10
  const [open10, setOpen10] = React.useState(false);
  const [scroll10, setScroll10] = React.useState("paper");

  const handleClickOpen10 = scrollType => () => {
    setOpen10(true);
    setScroll10(scrollType);
  };

  function handleClose10() {
    setOpen10(false);
  }

  // Example 11
  const [open11, setOpen11] = React.useState(false);

  const handleClickOpen11 = () => {
    setOpen11(true);
  };

  const handleClose11 = () => {
    setOpen11(false);
  };

  return (
    <>
      <Notice icon="flaticon-warning font-primary">
        <span>
          Dialogs inform users about a task and can contain critical
          information, require decisions, or involve multiple tasks
        </span>{" "}
        <span>
          For more info please check the components's official{" "}
          <a
            target="_blank"
            className="font-weight-bold"
            rel="noopener noreferrer"
            href="https://material-ui.com/components/dialogs/"
          >
            demos & documentation
          </a>
        </span>
      </Notice>

      <div className="row">
        <div className="col-md-6">
          <KTCodeExample
            jsCode={jsCode1}
            beforeCodeTitle="Simple Dialogs"
            codeBlockHeight="400px"
          >
            <span>
              Simple dialogs can provide additional details or actions about a
              list item. For example, they can display avatars, icons,
              clarifying subtext, or orthogonal actions (such as adding an
              account).
            </span>
            <div className="separator separator-dashed my-7"></div>
            <div>
              <Typography variant="subtitle1">
                Selected: {selectedValue}
              </Typography>
              <br />
              <Button
                variant="outlined"
                color="primary"
                onClick={handleClickOpen}
              >
                Open simple dialog
              </Button>
              <SimpleDialog
                selectedValue={selectedValue}
                open={open}
                onClose={handleClose}
              />
            </div>
          </KTCodeExample>

          <KTCodeExample
            jsCode={jsCode3}
            beforeCodeTitle="Transitions"
            codeBlockHeight="400px"
          >
            <span>
              You can also swap out the transition, the next example uses{" "}
              <code>Slide</code>.
            </span>
            <div className="separator separator-dashed my-7"></div>
            <div>
              <Button
                variant="outlined"
                color="primary"
                onClick={handleClickOpen3}
              >
                Slide in alert dialog
              </Button>
              <Dialog
                open={open3}
                TransitionComponent={Transition3}
                keepMounted
                onClose={handleClose3}
                aria-labelledby="alert-dialog-slide-title"
                aria-describedby="alert-dialog-slide-description"
              >
                <DialogTitle id="alert-dialog-slide-title">
                  {"Use Google's location service?"}
                </DialogTitle>
                <DialogContent>
                  <DialogContentText id="alert-dialog-slide-description">
                    Let Google help apps determine location. This means sending
                    anonymous location data to Google, even when no apps are
                    running.
                  </DialogContentText>
                </DialogContent>
                <DialogActions>
                  <Button onClick={handleClose3} color="primary">
                    Disagree
                  </Button>
                  <Button onClick={handleClose3} color="primary">
                    Agree
                  </Button>
                </DialogActions>
              </Dialog>
            </div>
          </KTCodeExample>

          <KTCodeExample
            jsCode={jsCode5}
            beforeCodeTitle="Customized dialogs"
            codeBlockHeight="400px"
          >
            <span>Here is an example of customizing the component.</span>
            <div className="separator separator-dashed my-7"></div>
            <div>
              <Button
                variant="outlined"
                color="secondary"
                onClick={handleClickOpen5}
              >
                Open dialog
              </Button>
              <Dialog
                onClose={handleClose5}
                aria-labelledby="customized-dialog-title"
                open={open5}
              >
                <DialogTitle5
                  id="customized-dialog-title"
                  onClose={handleClose5}
                >
                  Modal title
                </DialogTitle5>
                <DialogContent5 dividers>
                  <Typography gutterBottom>
                    Cras mattis consectetur purus sit amet fermentum. Cras justo
                    odio, dapibus ac facilisis in, egestas eget quam. Morbi leo
                    risus, porta ac consectetur ac, vestibulum at eros.
                  </Typography>
                  <Typography gutterBottom>
                    Praesent commodo cursus magna, vel scelerisque nisl
                    consectetur et. Vivamus sagittis lacus vel augue laoreet
                    rutrum faucibus dolor auctor.
                  </Typography>
                  <Typography gutterBottom>
                    Aenean lacinia bibendum nulla sed consectetur. Praesent
                    commodo cursus magna, vel scelerisque nisl consectetur et.
                    Donec sed odio dui. Donec ullamcorper nulla non metus auctor
                    fringilla.
                  </Typography>
                </DialogContent5>
                <DialogActions5>
                  <Button onClick={handleClose5} color="primary">
                    Save changes
                  </Button>
                </DialogActions5>
              </Dialog>
            </div>
          </KTCodeExample>

          <KTCodeExample
            jsCode={jsCode7}
            beforeCodeTitle="Optional sizes"
            codeBlockHeight="400px"
          >
            <span>
              You can set a dialog maximum width by using the{" "}
              <code>maxWidth</code> enumerable in combination with the{" "}
              <code>fullWidth</code> boolean. When the <code>fullWidth</code>{" "}
              property is true, the dialog will adapt based on the{" "}
              <code>maxWidth</code> value.
            </span>
            <div className="separator separator-dashed my-7"></div>
            <React.Fragment>
              <Button
                variant="outlined"
                color="primary"
                onClick={handleClickOpen7}
              >
                Open max-width dialog
              </Button>
              <Dialog
                fullWidth={fullWidth7}
                maxWidth={maxWidth7}
                open={open7}
                onClose={handleClose7}
                aria-labelledby="max-width-dialog-title"
              >
                <DialogTitle id="max-width-dialog-title">
                  Optional sizes
                </DialogTitle>
                <DialogContent>
                  <DialogContentText>
                    You can set my maximum width and whether to adapt or not.
                  </DialogContentText>
                  <form className={classes7.form} noValidate>
                    <FormControl className={classes7.formControl}>
                      <InputLabel htmlFor="max-width">maxWidth</InputLabel>
                      <Select
                        value={maxWidth7}
                        onChange={handleMaxWidthChange7}
                        inputProps={{
                          name: "max-width",
                          id: "max-width"
                        }}
                      >
                        <MenuItem value={false}>false</MenuItem>
                        <MenuItem value="xs">xs</MenuItem>
                        <MenuItem value="sm">sm</MenuItem>
                        <MenuItem value="md">md</MenuItem>
                        <MenuItem value="lg">lg</MenuItem>
                        <MenuItem value="xl">xl</MenuItem>
                      </Select>
                    </FormControl>
                    <FormControlLabel
                      className={classes7.formControlLabel}
                      control={
                        <Switch
                          checked={fullWidth7}
                          onChange={handleFullWidthChange7}
                          value="fullWidth"
                        />
                      }
                      label="Full width"
                    />
                  </form>
                </DialogContent>
                <DialogActions>
                  <Button onClick={handleClose7} color="primary">
                    Close
                  </Button>
                </DialogActions>
              </Dialog>
            </React.Fragment>
          </KTCodeExample>

          <KTCodeExample
            jsCode={jsCode9}
            beforeCodeTitle="Confirmation dialogs"
            codeBlockHeight="400px"
          >
            <span>
              Confirmation dialogs require users to explicitly confirm their
              choice before an option is committed. For example, users can
              listen to multiple ringtones but only make a final selection upon
              touching “OK”.
            </span>
            <div className="separator separator-dashed my-7"></div>
            <div className={classes9.root}>
              <List component="div" role="list">
                <ListItem button divider disabled role="listitem">
                  <ListItemText primary="Interruptions" />
                </ListItem>
                <ListItem
                  button
                  divider
                  aria-haspopup="true"
                  aria-controls="ringtone-menu"
                  aria-label="Phone ringtone"
                  onClick={handleClickListItem9}
                  role="listitem"
                >
                  <ListItemText primary="Phone ringtone" secondary={value9} />
                </ListItem>
                <ListItem button divider disabled role="listitem">
