/* eslint-disable no-restricted-imports */
import React from "react";
import {makeStyles} from "@material-ui/core/styles";
import {
  FormControlLabel,
  Switch,
  Collapse,
  Paper,
  Fade,
  Grow,
  Slide,
  Zoom
} from "@material-ui/core";
import {Notice, KTCodeExample} from "../../../../_metronic/_partials/controls";

// Example 1
const useStyles1 = makeStyles(theme => ({
  root: {
    height: 180
  },
  container: {
    display: "flex"
  },
  paper: {
    margin: theme.spacing(1)
  },
  svg: {
    width: 100,
    height: 100
  },
  polygon: {
    fill: theme.palette.common.white,
    stroke: theme.palette.divider,
    strokeWidth: 1
  }
}));

// Example 2
const useStyles2 = makeStyles(theme => ({
  root: {
    height: 180
  },
  container: {
    display: "flex"
  },
  paper: {
    margin: theme.spacing(1)
  },
  svg: {
    width: 100,
    height: 100
  },
  polygon: {
    fill: theme.palette.common.white,
    stroke: theme.palette.divider,
    strokeWidth: 1
  }
}));

// Example 3
const useStyles3 = makeStyles(theme => ({
  root: {
    height: 180
  },
  container: {
    display: "flex"
  },
  paper: {
    margin: theme.spacing(1)
  },
  svg: {
    width: 100,
    height: 100
  },
  polygon: {
    fill: theme.palette.common.white,
    stroke: theme.palette.divider,
    strokeWidth: 1
  }
}));

// Example 4
const useStyles4 = makeStyles(theme => ({
  root: {
    height: 180
  },
  wrapper: {
    width: 100 + theme.spacing(2)
  },
  paper: {
    zIndex: 1,
    position: "relative",
    margin: theme.spacing(1)
  },
  svg: {
    width: 100,
    height: 100
  },
  polygon: {
    fill: theme.palette.common.white,
    stroke: theme.palette.divider,
    strokeWidth: 1
  }
}));

// Example 5
const useStyles5 = makeStyles(theme => ({
  root: {
    height: 180
  },
  container: {
    display: "flex"
  },
  paper: {
    margin: theme.spacing(1)
  },
  svg: {
    width: 100,
    height: 100
  },
  polygon: {
    fill: theme.palette.common.white,
    stroke: theme.palette.divider,
    strokeWidth: 1
  }
}));

export default function TranstitionsExamplesPage() {
  // Example 1
  const classes1 = useStyles1();
  const [checked1, setChecked1] = React.useState(false);

  function handleChange1() {
    setChecked1(prev => !prev);
  }

  // Example 2
  const classes2 = useStyles2();
  const [checked2, setChecked2] = React.useState(false);

  function handleChange2() {
    setChecked2(prev => !prev);
  }

  // Example 3
  const classes3 = useStyles3();
  const [checked3, setChecked3] = React.useState(false);

  function handleChange3() {
    setChecked3(prev => !prev);
  }

  // Example 4
  const classes4 = useStyles4();
  const [checked4, setChecked4] = React.useState(false);

  function handleChange4() {
    setChecked4(prev => !prev);
  }

  // Example 5
  const classes5 = useStyles5();
  const [checked5, setChecked5] = React.useState(false);

  function handleChange5() {
    setChecked5(prev => !prev);
  }

  return (
    <>
      <Notice icon="flaticon-warning font-primary">
        <span>Transition helps make a UI expressive and easy to use.</span>{" "}
        <span>
          For more info please check the components's official{" "}
          <a
            target="_blank"
            className="font-weight-bold"
            rel="noopener noreferrer"
            href="https://material-ui.com/components/transitions/"
          >
            demos & documentation
          </a>
        </span>
      </Notice>

      <div className="row">
        <div className="col-md-6">
