/* eslint-disable no-restricted-imports */
import React from "react";
import {makeStyles, withStyles} from "@material-ui/core/styles";
import {
  Badge,
  IconButton,
  AppBar,
  Tabs,
  Tab,
  Typography,
  Button,
  FormGroup,
  FormControlLabel,
  Divider,
  Switch
} from "@material-ui/core";
import MailIcon from "@material-ui/icons/Mail";
import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";
import {Notice, KTCodeExample} from "../../../../_metronic/_partials/controls";

// Example 1
const useStyles1 = makeStyles(theme => ({
  margin: {
    margin: theme.spacing(2)
  },
  padding: {
    padding: theme.spacing(0, 2)
  }
}));

// Example 2
const useStyles2 = makeStyles(theme => ({
  margin: {
    margin: theme.spacing(2),
    marginRight: theme.spacing(3)
  }
}));

// Example 3
const useStyles3 = makeStyles(theme => ({
  margin: {
    margin: theme.spacing(2)
  }
}));

// Example 4
const useStyles4 = makeStyles(theme => ({
  root: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    width: "100%"
  },
  margin: {
    margin: theme.spacing(1)
  },
  divider: {
    width: "100%"
  },
  row: {
    marginTop: theme.spacing(2)
  }
}));

// Example 5
const StyledBadge = withStyles(theme => ({
  badge: {
    top: "50%",
    right: -3,
    // The border color match the background color.
    border: `2px solid ${
      theme.palette.type === "light"
        ? theme.palette.grey[200]
        : theme.palette.grey[900]
    }`
  }
}))(Badge);

export default function BadgesExamplesPage() {
  // Example 1
  const classes1 = useStyles1();

  // Example 2
  const classes2 = useStyles2();

  // Example 3
  const classes3 = useStyles3();

  // Example 4
  const classes4 = useStyles4();
  const [invisible4, setInvisible4] = React.useState(false);

  function handleBadgeVisibility4() {
    setInvisible4(!invisible4);
  }

  return (
    <>
      <Notice icon="flaticon-warning font-primary">
        <span>
          Badge generates a small badge to the top-right of its child(ren).
        </span>{" "}
        <span>
          For more info please check the components's official{" "}
          <a
            target="_blank"
            className="font-weight-bold"
            rel="noopener noreferrer"
            href="https://material-ui.com/components/badges/"
          >
            demos & documentation
          </a>
        </span>
      </Notice>

      <div className="row">
        <div className="col-md-6">
          <KTCodeExample jsCode={jsCode1} beforeCodeTitle="Simple Badges">
            <span>
              Examples of badges containing text, using primary and secondary
              colors. The badge is applied to its children.
            </span>
            <div className="separator separator-dashed my-7" />
            <div>
              <div>
                <Badge
                  className={classes1.margin}
                  badgeContent={4}
                  color="primary"
                >
                  <MailIcon />
                </Badge>
                <Badge
                  className={classes1.margin}
                  badgeContent={10}
                  color="secondary"
                >
                  <MailIcon />
                </Badge>
                <IconButton
                  aria-label="4 pending messages"
                  className={classes1.margin}
                >
                  <Badge badgeContent={4} color="primary">
                    <MailIcon />
                  </Badge>
                </IconButton>
              </div>
              <AppBar position="static" className={classes1.margin}>
                <Tabs value={0}>
                  <Tab
                    label={
                      <Badge
                        className={classes1.padding}
                        color="secondary"
                        badgeContent={4}
                      >
                        Item One
                      </Badge>
                    }
                  />
                  <Tab label="Item Two" />
                  <Tab label="Item Three" />
                </Tabs>
              </AppBar>
              <Badge
                color="primary"
                badgeContent={4}
                className={classes1.margin}
              >
                <Typography className={classes1.padding}>Typography</Typography>
              </Badge>
              <Badge
                color="primary"
                badgeContent={4}
                className={classes1.margin}
              >
                <Button variant="contained">Button</Button>
              </Badge>
            </div>
          </KTCodeExample>

          <KTCodeExample jsCode={jsCode3} beforeCodeTitle="Dot Badge">
            <span>
              The <code>dot</code> property changes a badge into a small dot.
              This can be used as a notification that something has changed
              without giving a count.
            </span>
            <div className="separator separator-dashed my-7"></div>
            <div>
              <div>
                <Badge
                  className={classes3.margin}
                  color="primary"
                  variant="dot"
                >
                  <MailIcon />
                </Badge>
                <Badge
                  className={classes3.margin}
                  color="secondary"
                  variant="dot"
                >
                  <MailIcon />
                </Badge>
              </div>
              <Badge color="primary" className={classes3.margin} variant="dot">
                <Typography>Typography</Typography>
              </Badge>
            </div>
          </KTCodeExample>

          <KTCodeExample jsCode={jsCode5} beforeCodeTitle="Customized badges">
            <span>Here is an example of customizing the component.</span>
            <div className="separator separator-dashed my-7"></div>
            <IconButton aria-label="Cart">
              <StyledBadge badgeContent={4} color="primary">
                <ShoppingCartIcon />
              </StyledBadge>
            </IconButton>
          </KTCodeExample>
        </div>
        <div className="col-md-6">
          <KTCodeExample jsCode={jsCode2} beforeCodeTitle="Maximum Value">
            <span>
              You can use the <code>max</code> property to cap the value of the
              badge content.
            </span>
            <div className="separator separator-dashed my-7"></div>
            <React.Fragment>
              <Badge
                className={classes2.margin}
                badgeContent={99}
                color="primary"
              >
                <MailIcon />
              </Badge>
              <Badge
                className={classes2.margin}
                badgeContent={100}
                color="primary"
              >
                <MailIcon />
              </Badge>
              <Badge
                className={classes2.margin}
                badgeContent={1000}
                max={999}
                color="primary"
              >
                <MailIcon />
              </Badge>
            </React.Fragment>
          </KTCodeExample>

          <KTCodeExample jsCode={jsCode4} beforeCodeTitle="Badge visibility">
            <span className="pb-3">
              The visibility of badges can be controlled using the invisible
              property.
            </span>
            <div>
              The badge auto hides with badgeContent is zero. You can override
              this with the <code>showZero</code> property.
            </div>
            <div className="separator separator-dashed my-7"></div>
            <div className={classes4.root}>
              <div className={classes4.row}>
                <Badge
                  color="secondary"
                  badgeContent={4}
                  invisible={invisible4}
                  className={classes4.margin}
                >
                  <MailIcon />
                </Badge>
                <Badge
                  color="secondary"
                  variant="dot"
                  invisible={invisible4}
                  className={classes4.margin}
                >
                  <MailIcon />
                </Badge>
              </div>
              <FormGroup row>
                <FormControlLabel
                  control={
                    <Switch
                      color="primary"
                      checked={!invisible4}
                      onChange={handleBadgeVisibility4}
                    />
                  }
                  label="Show Badge"
                />
              </FormGroup>
              <Divider className={classes4.divider} />
              <div className={classes4.row}>
                <Badge
                  color="secondary"
                  badgeContent={0}
                  className={classes4.margin}
                >
                  <MailIcon />
                </Badge>
                <Badge
                  color="secondary"
                  badgeContent={0}
                  showZero
                  className={classes4.margin}
                >
                  <MailIcon />
                </Badge>
              </div>
            </div>
          </KTCodeExample>
        </div>
      </div>
    </>
  );
}

const jsCode1 = `
